import React, { Component } from "react";
import TNode from "./TNode";
import "../grid.css";

const startRow = 9;
const startCol = 9;
const finishRow = 9;
const finishCol = 40;
const isStart = true;
const isFinish = false;

export default class PathfindingVisualizer extends Component {
  constructor() {
    super();
    this.state = {
      grid: [],
      mouseIsPressed: false
    };
  }

  componentDidMount() {
    const grid = getGrid();
    this.setState({ grid });
  }

  handleMouseClick(row, col) {
    const newGrid = getNewGrid(this.state.grid, row, col, isStart, isFinish);
    this.setState({grid: newGrid});
  }

  render() {
    const {grid} = this.state;

    return (
      <div className="grid">
        {grid.map((row, rowIndex) => {
          return (
            <div key={rowIndex}>
              {row.map((node, nodeIndex) => {
                const { row, col, isStart, isFinish, isVisited } = node;
                return (
                  <TNode
                    key={nodeIndex}
                    row={row}
                    col={col}
                    isStart={isStart}
                    isFinish={isFinish}
                    isVisited={isVisited}
                    handleMouseClick={(row, col) =>
                      this.handleMouseClick(row, col)}
                  ></TNode>
                );
              })}
            </div>
          );
        })}
      </div>
    );
  }
}

const getGrid = () => {
  const grid = [];

  for (let i = 0; i < 20; i++) {
    const currentRow = [];
    for (let j = 0; j < 50; j++) {
      currentRow.push(createNode(i, j));
    }
    grid.push(currentRow);
  }

  return grid;
};

const createNode = (row, col) => {
  return {
    row: row,
    col: col,
    // isStart: row == startRow && col == startCol,
    // isFinish: row == finishRow && col == finishCol,
    isStart: false,
    isFinish: false,
    isVisited: false,
  };
};

const getNewGrid = (grid, row, col, isStart, isFinish) => {
  if(isStart) {
    const newGrid = grid.slice();
    const node = newGrid[row][col];
    const newNode = {
      ...node,
      isStart: isStart
    };

    newGrid[row][col] = newNode;
    isStart = false;
    isFinish = true;
    return newGrid;
  }

  if(isFinish) {
    const newGrid = grid.slice();
    const node = newGrid[row][col];
    const newNode = {
      ...node,
      isFinish: isFinish
    };

    newGrid[row][col] = newNode;
    isFinish = false;
    return newGrid;
  }
}
